package com.javaex.practice;

public class Ex09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int x=0; x<10; x++) {
			for(int y=x+1; y<=x+10; y++) {
				System.out.print(y);
				System.out.print("\t");
			}
			System.out.println("");
		}
	}

}
